import React, { useState } from 'react';

export default function ObjState() {
  const [state, setState] = useState({ teacher: '정새싹' });
  console.log(state);

  return (
    <div>
      <button
        onClick={() => {
          state.teacher = '정새싹'
            ? (state.teacher = 'SeSAC Jeong')
            : (state.teacher = '정새싹');
          const copyObj = { ...state };
          setState(copyObj);
          console.log(state);
        }}
      >
        영어로!
      </button>
      <br />
      <span>{state.teacher}</span>
    </div>
  );
}
